mapper.select:
UPDATE team_image INNER JOIN team_ex ON team_image.image_id = team_ex.representative_image_id
    SET team_image.team_id = team_ex.team_id
WHERE team_image.team_image_url = #{URL};

SELECT *
FROM team
WHERE category_id = #{categoryId}
AND team_location = #{teamLocation};

SELECT team_id, team_name, team_image_url
FROM team
WHERE category_id = ? AND status = 'NORMAL';
